/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const url="https://deckofcardsapi.com/api/deck/new/draw/"
  const response = useAxios(url)
  // const url="https://deckofcardsapi.com/api/deck/new/draw/"
  console.log(response.data)
