import React, { useState } from "react";
import {v1 as uuid} from "uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import {useAxios,useLocalStorage} from "./hooks/useAxios"

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  // const url="https://deckofcardsapi.com/api/deck/new/draw/"
  const [cards, addCard,clearCards] = useAxios('cards',"https://deckofcardsapi.com/api/deck/new/draw/" )
  // const url="https://deckofcardsapi.com/api/deck/new/draw/"
  // console.log(response.deck_id)
  console.log(cards)
  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={addCard}>Add a playing card!</button>
        <button onClick={clearCards}>Clear Table</button>
      </div>
      <div className="PlayingCardList-card-area">
        {cards.map(card =>(

          <PlayingCard key={card.id} front={card.card.image}/>
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;
